```java
public class BinarySearch {

    // Iterative approach
    public static int binarySearchIterative(int[] arr, int target) {
        int left = 0;
        int right = arr.length - 1;

        while (left <= right) {
            int mid = left + (right - left) / 2; // To prevent potential overflow for very large left and right

            // Check if target is present at mid
            if (arr[mid] == target) {
                return mid; // Target found, return its index
            }

            // If target is greater, ignore left half
            if (arr[mid] < target) {
                left = mid + 1;
            }
            // If target is smaller, ignore right half
            else {
                right = mid - 1;
            }
        }

        // Target not found in the array
        return -1;
    }

    // Recursive approach
    public static int binarySearchRecursive(int[] arr, int target, int left, int right) {
        // Base case: If the search space is invalid (left > right), target is not found
        if (left > right) {
            return -1;
        }

        int mid = left + (right - left) / 2;

        // Base case: Target found at mid
        if (arr[mid] == target) {
            return mid;
        }

        // If target is greater, search in the right half
        if (arr[mid] < target) {
            return binarySearchRecursive(arr, target, mid + 1, right);
        }
        // If target is smaller, search in the left half
        else {
            return binarySearchRecursive(arr, target, left, mid - 1);
        }
    }

    public static void main(String[] args) {
        int[] sortedArray = {2, 5, 8, 12, 16, 23, 38, 56, 72, 91};
        int target1 = 23;
        int target2 = 7;

        // Test iterative binary search
        int resultIterative1 = binarySearchIterative(sortedArray, target1);
        if (resultIterative1 != -1) {
            System.out.println("Iterative: Element " + target1 + " found at index " + resultIterative1);
        } else {
            System.out.println("Iterative: Element " + target1 + " not found in the array");
        }

        int resultIterative2 = binarySearchIterative(sortedArray, target2);
        if (resultIterative2 != -1) {
            System.out.println("Iterative: Element " + target2 + " found at index " + resultIterative2);
        } else {
            System.out.println("Iterative: Element " + target2 + " not found in the array");
        }

        System.out.println("\n----------------------------------\n");

        // Test recursive binary search
        int resultRecursive1 = binarySearchRecursive(sortedArray, target1, 0, sortedArray.length - 1);
        if (resultRecursive1 != -1) {
            System.out.println("Recursive: Element " + target1 + " found at index " + resultRecursive1);
        } else {
            System.out.println("Recursive: Element " + target1 + " not found in the array");
        }

        int resultRecursive2 = binarySearchRecursive(sortedArray, target2, 0, sortedArray.length - 1);
        if (resultRecursive2 != -1) {
            System.out.println("Recursive: Element " + target2 + " found at index " + resultRecursive2);
        } else {
            System.out.println("Recursive: Element " + target2 + " not found in the array");
        }
    }
}
