#include <iostream>
#include <vector>
#include <algorithm> // Required for std::swap if not implementing manually

// Function to perform Bubble Sort on a vector of integers
void bubbleSort(std::vector<int>& arr) {
    int n = arr.size();
    // Outer loop for passes
    for (int i = 0; i < n - 1; ++i) {
        // Inner loop for comparisons and swaps in each pass
        // The last i elements are already in place, so we iterate up to n - 1 - i
        for (int j = 0; j < n - 1 - i; ++j) {
            // If the current element is greater than the next element, swap them
            if (arr[j] > arr[j + 1]) {
                std::swap(arr[j], arr[j + 1]); // Using std::swap for efficiency and readability
            }
        }
    }
}

// Function to print the elements of a vector
void printArray(const std::vector<int>& arr) {
    for (int x : arr) {
        std::cout << x << " ";
    }
    std::cout << std::endl;
}

int main() {
    // Example usage of Bubble Sort
    std::vector<int> myVector = {64, 34, 25, 12, 22, 11, 90};

    std::cout << "Original array: ";
    printArray(myVector);

    bubbleSort(myVector);

    std::cout << "Sorted array: ";
    printArray(myVector);

    // Another example
    std::vector<int> anotherVector = {5, 1, 4, 2, 8};
    std::cout << "\nOriginal array: ";
    printArray(anotherVector);

    bubbleSort(anotherVector);

    std::cout << "Sorted array: ";
    printArray(anotherVector);

    return 0;
}

