// Function to find the maximum in each sliding window
void maxSlidingWindow(int* nums, int numsSize, int k, int* result) {
    if (numsSize == 0 || k == 0) return;

    // deque stores indices of elements
    // front is head, rear is tail
    int* dq = (int*)malloc(numsSize * sizeof(int));
    int front = 0;
    int rear = -1; // Pointer to the last element in deque

    int resultIndex = 0;

    for (int i = 0; i < numsSize; i++) {
        // Remove elements from the front of the deque that are out of the current window
        if (front <= rear && dq[front] <= i - k) {
            front++;
        }

        // Remove elements from the back of the deque that are smaller than or equal to the current element
        // This maintains the property that deque elements are in decreasing order
        while (front <= rear && nums[dq[rear]] <= nums[i]) {
            rear--;
        }

        // Add current element's index to the back of the deque
        rear++;
        dq[rear] = i;

        // If the window has formed (i.e., we have processed at least k elements),
        // the maximum element for this window is at the front of the deque
        if (i >= k - 1) {
            result[resultIndex++] = nums[dq[front]];
        }
    }
    free(dq);
}

// Main function to test the maxSlidingWindow function
